	public static String largestNumber(final List<Integer> A) {
		Collections.sort(A, new Comparator<Integer>() {
			@Override
			public int compare(Integer num1, Integer num2) {
				String numStr1 = "" + num1 + num2;
				String numStr2 = "" + num1 + num2;
				if (Double.parseDouble(numStr1) < Double.parseDouble(numStr2))
					return 1;
				if (Double.parseDouble(numStr1) > Double.parseDouble(numStr2))
					return -1;
				return 0;
			}

		});
		StringBuilder result = new StringBuilder();
		boolean isZeroFinished = false;
		for (int i = 0; i < A.size(); i++) {
			if (A.get(i) > 0)
				isZeroFinished = true;
			if (A.get(i) != 0 && isZeroFinished)
				result.append(A.get(i));
		}
		result = result.isEmpty() ? result.append("0") : result;
		return result.toString();
	}
